import { NavLink } from "react-router-dom";

import classNames from "classnames";

const Navigation = (props) => {
  const {
    name,
    setName,
    verified,
    setVerified,
    signOn,
    setSignOn,
    displayCart,
    setDisplayCart,
    employee
  } = props;

  const signOff = () => {
    setVerified(!verified);
    setTimeout(() => {
      setName("Guest");
    }, 250);
  };

  return (
    <div>
      <div className="navigation">
        <span className="navigation--title">The Bookstore</span>

        <span
          className={classNames(
            { "display-register": !verified },
            { "hide-register": verified }
          )}
        >
          Register <NavLink to="/signUp"> here!</NavLink>
        </span>
        <span></span>

        <div className="navigation--buttons">
          {!verified ? (
            <button className="sign-on" onClick={() => setSignOn(!signOn)}>
              Sign On
            </button>
          ) : (
            <button className="sign-on" onClick={() => signOff()}>
              Sign Off
            </button>
          )}
        </div>
      </div>
      <div className="navigation--secondary-nav">Nav buttons go here</div>
      {employee === true ?<div className="navigation--employee-nav">Employee Nav goes here when not hidden</div>:null}
    </div>
  );
};
export default Navigation;
