import { NavLink } from "react-router-dom";
import cart from "../../assets/images/icons8-shopping-cart-64.png";

import classNames from "classnames";

const Navigation = (props) => {
  const {
    name,
    setName,
    verified,
    setVerified,
    signOn,
    setSignOn,
    displayCart,
    setDisplayCart,
  } = props;

  const signOff = () => {
    setVerified(!verified);
    setTimeout(() => {
      setName("Guest");
    }, 250);
  };

  return (
    <div className="navigation">
      <span className="welcome">
        <NavLink to="/">

        </NavLink>
        <span className="welcome__greeting">Welcome {name}</span>
      </span>
      <span
        className={classNames(
          { "display-register": !verified },
          { "hide-register": verified }
        )}
      >
        Sign up for our Rewards Program <NavLink to="/signUp"> here!</NavLink>
      </span>
      <span></span>

      <div className="navigation--buttons">
        {!verified ? (
          <button className="sign-on" onClick={() => setSignOn(!signOn)}>
            Sign On
          </button>
        ) : (
          <button className="sign-on" onClick={() => signOff()}>
            Sign Off
          </button>
        )}
        ;
        <NavLink>
          <img
            className="cart-icon"
            src={cart}
            alt="cart"
            onClick={() => setDisplayCart(!displayCart)}
          />
        </NavLink>
      </div>
    </div>
  );
};
export default Navigation;
